(()=>{document.addEventListener("scroll",()=>{let e=document.querySelector(".navbar");window.scrollY>100?e.classList.add("scrolled"):e.classList.remove("scrolled")});document.querySelector(".navbar-toggler").addEventListener("click",function(e){e.stopPropagation(),document.querySelectorAll(".navbar-toggler .hamburger-icon").forEach(t=>{t.classList.toggle("open")});let o=document.querySelector(".navbar-collapse");o.classList.contains("show")?bootstrap.Collapse.getInstance(o).hide():bootstrap.Collapse.getInstance(o).show()});document.addEventListener("click",function(e){let n=document.querySelector(".navbar"),o=document.querySelector(".navbar-toggler"),t=document.querySelector(".navbar-collapse");!n.contains(e.target)&&!o.contains(e.target)&&(document.querySelectorAll(".navbar-toggler .hamburger-icon").forEach(a=>{a.classList.remove("open")}),t.classList.contains("show")&&bootstrap.Collapse.getInstance(t).hide())});document.querySelector(".navbar").addEventListener("click",function(e){e.stopPropagation()});})();
